mapper.select:
SELECT * FROM category 
WHERE name = #{name} 
AND is_visible = 1

WITH RECURSIVE category_path AS ( -- 현재 카테고리 SELECT category_id, parent_id, name, level FROM category WHERE category_id = #{categoryId}
UNION ALL
-- 부모 카테고리들을 재귀적으로 찾기 SELECT c.category_id, c.parent_id, c.name, c.level FROM category c JOIN category_path cp ON c.category_id = cp.parent_id ) SELECT category_id as categoryId, name, level FROM category_path ORDER BY level ASC
