mapper.select:
SELECT oi.order_item_id AS orderItemId, oi.order_id AS orderId, o.order_code AS orderCode, o.ship_recipient AS shipRecipient, o.ship_phone AS shipPhone, CONCAT(o.ship_postcode, ' ', o.ship_line1, ' ', o.ship_line2) AS shipAddress, o.note AS note, o.created_at AS orderDate, oi.tracking_no AS trackingNo, oi.carrier_name AS carrierName, oi.status AS status, (SELECT p2.name FROM order_item oi2 JOIN product p2 ON oi2.product_id = p2.product_id WHERE oi2.order_id = oi.order_id AND oi2.status = oi.status LIMIT 1) AS representProductName, (SELECT GROUP_CONCAT(p3.name SEPARATOR ', ') FROM order_item oi3 JOIN product p3 ON oi3.product_id = p3.product_id WHERE oi3.order_id = oi.order_id AND oi3.status = oi.status) AS allProductNames, CASE WHEN oi.status IN ('SHIPPING','DELIVERED','COLLECTING','EXCHANGE SHIPPING') THEN oi.status ELSE 'DELIVERED' END AS displayStatus FROM order_item oi INNER JOIN orders o ON oi.order_id = o.orders_id
oi.order_id = #{orderId} AND oi.status = #{status} AND oi.brand_id = #{brandId}
LIMIT 1

SELECT oi.order_item_id AS orderItemId, oi.product_id AS productId, oi.option_id AS optionId, p.name AS productName, po.option_value AS optionName, oi.quantity AS quantity, oi.status AS status, CASE WHEN oi.status IN ('SHIPPING','DELIVERED','COLLECTING','EXCHANGE SHIPPING') THEN oi.status ELSE 'DELIVERED' END AS displayStatus FROM order_item oi INNER JOIN product p ON oi.product_id = p.product_id LEFT JOIN product_option po ON oi.option_id = po.product_option_id
oi.order_id = #{orderId} AND oi.status = #{status} AND oi.brand_id = #{brandId}
ORDER BY oi.order_item_id ASC

SELECT DATE(o.created_at) AS date, p.category1_id AS categoryId, c.name AS categoryName, COALESCE(SUM(oi.line_subtotal - oi.discount), 0) AS amount FROM order_item oi JOIN orders o ON oi.order_id = o.orders_id JOIN product p ON oi.product_id = p.product_id JOIN category c ON p.category1_id = c.category_id WHERE oi.brand_id = #{brandId} AND oi.status = 'CONFIRMED'
AND DATE(o.created_at) >= #{startDate}
AND DATE(o.created_at) <= #{endDate}
AND p.category1_id IN
#{catId}
GROUP BY DATE(o.created_at), p.category1_id, c.name ORDER BY DATE(o.created_at) ASC
