mapper.select:
SELECT * FROM product
WHERE category_id = #{categoryId}
AND
is_visible = 1
ORDER BY created_at DESC
LIMIT #{offset}, #{limit}

SELECT * FROM product
WHERE brand_id = #{brandId}
AND
is_visible = 1
ORDER BY created_at DESC
LIMIT #{offset}, #{limit}

SELECT product_id as productId, name, price, order_limit as orderLimit, stock_qty as stockQty, has_option as hasOption, shipping_method as shippingMethod FROM product WHERE product_id IN
#{productId}
AND is_visible = 1
